import React, { useState } from 'react';
import './App.css';
import moment from 'moment';
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import 'firebase/compat/firestore';
import Icon from './pdficon.js';
import Main from './main.js';

import { useAuthState } from 'react-firebase-hooks/auth';
import { useCollectionData } from 'react-firebase-hooks/firestore';

if (!firebase.apps.length) {

  firebase.initializeApp({
        apiKey: "AIzaSyDUWZQp7u-MI3fxDaIeohNDr7vmy7mUIR4",
	authDomain: "recipes-394c2.firebaseapp.com",
	projectId: "recipes-394c2",
	storageBucket: "recipes-394c2.appspot.com",
	messagingSenderId: "83409877153",
	appId: "1:83409877153:web:68763b948cbd823c12d8e0",
	measurementId: "G-YKQLZJCXYM"
  })

} else {
  firebase.app()
}

const auth = firebase.auth();
const firestore = firebase.firestore();

function App() {
  const [user]  = useAuthState(auth);

  return (
    <div className="container">
      {
        user ? <Main auth={auth} firestore={firestore}/>: <Signin />
      }
    </div>
  );
}

const Signin = () => {
  const signInWithGoogle = ()=> {
    const provider = new firebase.auth.GoogleAuthProvider();
  auth.signInWithPopup(provider).then(async result => {
    var user = result.user;
    if (user) {
      const userRef = firestore.collection('users');
      const query = userRef.where("id", "==", user.uid).limit(1);
      const snapshot = await query.get();

    if (snapshot.empty) {
          userRef.add({
            id:user.uid, 
            uname:user.displayName, 
            status:"user"
          });
        }



    }
 }).catch(e => {
 });

  }

  const googleLogo = new URL("./btn_google_4.png",import.meta.url)

  return (
    <div className='signin' style={{
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      height: '100vh',
    }}>
      <img src={googleLogo}
      onClick ={signInWithGoogle}
      className='btn btn-primary btn-lg'/>
            </div>
  )
}


export default App;
