import { relativeTimeRounding } from 'moment';
import React, { Component } from 'react';
import {doc, addDoc, getDoc, updateDoc} from "firebase/firestore"


class Comment extends Component {

  constructor(props) {
    super(props)

    if (this.props.value!=="") {
      this.state = {
        value:this.props.value, 
        isInEditMode:false,
        recipe:this.props.recipe,
        rank:this.props.commentRank
      }
    } else {
      this.state = {
        value:"###################################################", 
        isInEditMode:false,
        recipe:this.props.recipe,
        rank:this.props.commentRank
      }
    }

    this.changeEditMode = this.changeEditMode.bind(this)
    this.renderEditView = this.renderEditView.bind(this)
    this.renderDefaultView = this.renderDefaultView.bind(this)
    this.updateComponentValue = this.updateComponentValue.bind(this)


}

changeEditMode() {
  this.setState(
    {
      isInEditMode:!this.state.isInEditMode
    }
  )
}

updateComponentValue = async e => {
  const newValue = this.refs.theTextInput.value
  const recipe = await getDoc(this.state.recipe);
  var comments = recipe.data().comments

  if (e.key === "Enter" && newValue !=="") {
    this.setState({
    isInEditMode:false,
    value: newValue}).then(comments[this.state.rank] = this.state.value).then(
    await updateDoc(this.state.recipe,{comments:comments})
    )
    }

  if (e.key === "Enter" && newValue ==="") {
    this.setState({
    isInEditMode:false,
    value: ""})
    comments.splice(this.state.rank,1)
    comments.push("###################################################")
    await updateDoc(this.state.recipe,{comments:comments})
}
}

renderEditView() {

  return <div>
    <input type="text" 
           defaultValue={this.state.value}
           ref="theTextInput" onKeyPress={this.updateComponentValue}/>
  </div>
}

renderDefaultView() {
  return <div onDoubleClick = {this.changeEditMode}> 
  {this.state.value}
</div>
}

  render() {
    return this.state.isInEditMode ? 
     this.renderEditView() : this.renderDefaultView()

  }
}

export default Comment;
