import { relativeTimeRounding } from 'moment';
import React, { Component, createRef } from 'react';
import {doc, addDoc, getDoc, updateDoc} from "firebase/firestore"


class CommentList extends Component {

  constructor(props) {
    super(props)

    const valueLength = this.props.value.length
    const isInEditMode=[]
    for (let i=0; i < valueLength; i++) {
      isInEditMode.push(false)
    }


    this.state = {
        value:this.props.value, 
        isInEditMode:isInEditMode,
        recipe:this.props.recipe, 
        someRef:React.createRef()
      }

    this.changeEditMode = this.changeEditMode.bind(this)
    this.renderEditView = this.renderEditView.bind(this)
    this.renderDefaultView = this.renderDefaultView.bind(this)
    this.updateComponentValue = this.updateComponentValue.bind(this)


}

changeEditMode = (j) => {
  const newEditMode=[]
  const valueLength = this.props.value.length
  for (let i=0; i < valueLength; i++) {
    if (i===j) {
      newEditMode.push(true)
    } else {
      newEditMode.push(false)
    }
  }

  this.setState(
    {
      isInEditMode:newEditMode
    }
  )
}

updateComponentValue = async (e,rank) => {
  const node = someRef.current
  const newValue = this.refs.nameDiv.value
  const recipe = await getDoc(this.state.recipe);
  var comments = recipe.data().comments

  console.log("second checkpoint")

  const newEditMode=[]
  const valueLength = this.props.value.length
  for (let i=0; i < valueLength; i++) {
      newEditMode.push(false) 
    }

  if (e.key === "Enter" && newValue !=="") {
    comments[rank] = newValue

    this.setState({
        value:comments})
    this.setState({
        isInEditMode:newEditMode})

    await updateDoc(this.state.recipe,{comments:comments})
    }


  if (e.key === "Enter" && newValue ==="") {
    comments.splice(this.state.rank,1)
    comments.push("###################################################")
    this.setState({
    isInEditMode:newEditMode})
    this.setState({
      value:comments})

    await updateDoc(this.state.recipe,{comments:comments})
}
}

renderEditView = (rank) => {

  const someRef = React.createRef()

  return <div>
    <input type="text" 
           defaultValue={this.state.value[rank]}
           ref={someRef} onKeyPress={(e) => this.updateComponentValue(e,rank)}/>

  </div>
}

renderDefaultView = (rank) => {
  return <div onDoubleClick = {this.changeEditMode(rank)}> 
  {this.state.value[rank]}

</div>
}

render() {     

    return <ol>{this.state.value.map((comment,rank) => (
      <li key={rank}> {this.state.isInEditMode[rank] ? 
      this.renderEditView(rank) : this.renderDefaultView(rank)} </li>
    ))}
    </ol>

  }
}

export default CommentList;
