import { db } from "./firebase.config"
import { useState, useEffect } from "react"
import {collection, onSnapshot, doc, addDoc, deleteDoc, setDoc , updateDoc} from "firebase/firestore"
import { v4 } from 'uuid';

import Comment from './editable.js';

function App() {

  const [recipes, setRecipes] = useState([])
  const [comments, setComments] = useState([])

  const [form, setForm] = useState({
    title: "",
    description: "",
    ingredients: [],
    steps: []
  })
  const [popupActive, setPopupActive] = useState(false)

  const [currentRecipe,setCurrentRecipe]=useState("")

  const [isInEditMode,setIsInEditMode]=useState(false)

  const recipesCollectionRef = collection(db,"recipes")
  const commentsCollectionRef = collection(db,"comments")

  useEffect(()=>{
      onSnapshot(recipesCollectionRef, snapshot => {
        setRecipes(snapshot.docs.map(doc => {
          return {
            id:doc.id,
            ...doc.data()
          }
        }))
      }
      )
      onSnapshot(commentsCollectionRef, snapshot => {
        setComments(snapshot.docs.map(doc => {
          return {
            id:doc.id,
            ...doc.data()
          }
        }))
      }
      )
    }, []
  )

  const handleView = id => {
    const recipesClone = [...recipes]

    recipesClone.forEach(async recipe => {
      const recipeRef = doc(db,"recipes",recipe.id)
      if (recipe.id === id) {
        await updateDoc(recipeRef,{viewing:!recipe.viewing});
      } else {
        await updateDoc(recipeRef,{viewing:false});
      }
    })

    setRecipes(recipesClone)
  }

  const handleSubmit = async e => {
    e.preventDefault()

    if (!form.title || !form.description || !form.ingredients || !form.steps) {
      alert("Please fill out all fields")
      return 
    }
    if (!isInEditMode) {
      var x = v4();
      await setDoc(doc(db, "recipes", x),{...form,viewing:false})
      addDoc(recipesCollectionRef,{...form,viewing:false})
      addDoc(commentsCollectionRef,{recipedIx:x,text:"############################################################"})
    } 
    if (isInEditMode) {
      const recipeRef = doc(db,"recipes",currentRecipe)
      await updateDoc(recipeRef,form);
    }

    setForm({
      title:"",
      description:"",
      ingredients:[],
      steps:[]
    })

    setPopupActive(false)
  }

  const handleIngredient = (e,i) => {
    const ingredientsClone = [...form.ingredients]

    ingredientsClone[i] = e.target.value

    setForm({
      ...form,
      ingredients:ingredientsClone
    })
  }

  const handleStep = (e,i) => {
    const stepsClone = [...form.steps]

    stepsClone[i] = e.target.value

    setForm({
      ...form,
      steps:stepsClone
    })
  }

  const handleIngredientCount = () => {
    setForm({
      ...form,
      ingredients: [...form.ingredients, ""]
    })
  }

  const handleStepCount = () => {
    setForm({
      ...form,
      steps: [...form.steps, ""]
    })
  }

  const handleCancel = () => {setPopupActive(false)
    setForm({
    title:"",
    description:"",
    ingredients:[],
    steps:[]
  })
}



  const removeRecipe = id => {
    deleteDoc(doc(db,"recipes",id))
  }


  return (
    <div className="App">
      <h1>S. and J. Moon-Gangloff's recipes list</h1>

      <button type="button" onClick ={()=> {
            setPopupActive(!popupActive)
          }}>Add recipe</button>

      <div className="recipes">
        <ol>
        { recipes.map((recipe,i) => (<li className="recipe" style={{backgroundColor:"rgba(27,17,19,0.75)",width:"80vw"}} key={recipe.id}>
          <h3 style={{color:"#feae00"}}>{ recipe.title }</h3>

          <p dangerouslySetInnerHTML={{ __html: recipe.description }} style={{color:"#bdbb65"}}></p>

          { recipe.viewing && <div>
            <h4>Ingredients</h4>
            <ul>
              {recipe.ingredients.map((ingredient,i) => (
                <li key={i}>{ ingredient }</li>
              ))}
            </ul>

            <h4>Steps</h4>
            <ol>
              {recipe.steps.map((step,i) => (
                <li key={i}><span style={{color:"#feae00"}}>{i+". "}</span><span>{ step }</span></li>
              ))}
            </ol>

            <h4>Comments</h4>

            <ol>{comments.map((comment,i) => (
                <li key={i}> {(comment.recipeId === recipe.id) ? <Comment id={comment.id} text={comment.text}/>   : <div></div>} </li>
                      ))}
            </ol>

          </div>}

          <div className = "buttons">
            <button onClick={() => handleView(recipe.id)}>View { recipe.viewing ? 'less' : 'more'}</button> 
            <button onClick={() => {setForm({title:recipe.title,description:recipe.description,ingredients:recipe.ingredients,steps:recipe.steps}) 
                                    setPopupActive(!popupActive)
                                    setCurrentRecipe(recipe.id)
                                    setIsInEditMode(true)
                                    }}>Edit recipe</button> 
            <button className="remove" onClick={() => removeRecipe(recipe.id)}>Remove recipe</button> 
          </div>
        </li>))}
        </ol>
      </div>

      { popupActive && <div className ="popup">
              <div className="popup-inner">
                <h2>Add a new recipe</h2>

                <form onSubmit = {handleSubmit}> 

                  <div className="form-group">
                    <label>Title</label> 
                    <input 
                      type="text" 
                      value={form.title} 
                      onChange = {e => setForm({...form,title:e.target.value})}/>
                  </div>

                  <div className="form-group">
                    <label>Description</label> 
                    <textarea
                      type="text" 
                      value={form.description} 
                      onChange = {e => setForm({...form,description:e.target.value})}/>
                  </div>

                  <div className="form-group">
                    <label>Ingredients</label> 
                    {
                      form.ingredients.map((ingredient,i) => (
                        <textarea
                      type="text" 
                      key={i}
                      value={ingredient} 
                      onChange = {e => handleIngredient(e,i)}/>
                      ))
                    }

                    <button type="button" onClick ={handleIngredientCount}>Add ingredient</button>

                  </div>

                  <div className="form-group">
                    <label>Steps</label> 
                    {
                      form.steps.map((step,i) => (
                        <textarea
                      type="textarea" 
                      key={i}
                      value={step} 
                      onChange = {e => handleStep(e,i)}/>
                      ))
                    }

                    <button type="button" onClick ={handleStepCount}>Add step</button>

                  </div>

                  <div className="buttons">
                    <button type="submit">Submit</button>
                    <button type="button" class="remove" onClick={handleCancel}>Cancel</button>
                  </div>

                </form>
              </div>
        </div>}
    </div>
  );
}

export default App;
