import { db } from "./firebase.config"
import { relativeTimeRounding } from 'moment';
import React, { Component, createRef } from 'react';
import {collection, doc, addDoc, getDoc, updateDoc} from "firebase/firestore"


class Comment extends Component {

  constructor(props) {
    super(props)

    this.state = {
        id:this.props.id, 
        isInEditMode:false
      }

    this.changeEditMode = this.changeEditMode.bind(this)
    this.renderEditView = this.renderEditView.bind(this)
    this.renderDefaultView = this.renderDefaultView.bind(this)
    this.updateComponentValue = this.updateComponentValue.bind(this)


}


changeEditMode() {
  this.setState(
    {
      isInEditMode:!this.state.isInEditMode
    }
  )
}



updateComponentValue(e) {
  const commentsCollectionRef = collection(db,"comments")
  if (e.key === "Enter") {
    commentsCollectionRef.doc(this.state.id).update({text:this.refs.theTextInput.value});
    this.setState({
    isInEditMode:false
  })

  }
}

renderEditView() {

  return <div>
    <input type="text" 
           defaultValue={""}
           ref="theTextInput" onKeyPress={this.updateComponentValue}/>
  </div>
}

renderDefaultView = async () => {
  const docRef = doc(db, "comments", this.state.id)
  const docSnap = await getDoc(docRef)
  return <div onDoubleClick = {this.changeEditMode}> 
  {docSnap.data().text.value}
</div>
}

  render() {
    return this.state.isInEditMode ? 
     this.renderEditView() : this.renderDefaultView()

  }
}

export default Comment;
